mapper.update:
UPDATE user
SET NAME = #{userName},SYS_NAME = #{sysName},PASSWORD = #{passWord}
WHERE  USER_ID = #{userId}

mapper.select:
SELECT user_id as userId, name as userName, sys_name as sysName,password as passWord
FROM user
WHERE user_id=#{userId}

SELECT user_id as userId, name as userName, sys_name as sysName,password as passWord
FROM user
WHERE sys_name = #{sysName}
